<template>
  <div class="container">
  <div class="columns">
    <div class="column two-thirds">
      <ol
        class="repo-list"
        id="blogol"
      >
        <li
          class="repo-list-item"
          v-for="blog in indexblogs"
          :key="blog.index"
        >
          <h3 class="repo-list-name">
            <a @click="toDetail(blog.id)">{{blog.id}} 、 {{ blog.title }}</a>
          </h3>
          <p class="repo-list-description">
            摘要:{{ blog.summary }}
          </p>
          <p class="repo-list-meta">
          <span class="meta-info">
            <i class="el-icon-alarm-clock" />
            {{ blog.publish_time }}
          </span>
            <span class="meta-info">
              <i class="el-icon-notebook-1"/>
            {{ blog.category_name }}
          </span>
          </p>
        </li>
      </ol>
    <div class="block">
      <el-pagination
        @size-change="handleSizeChange"
        @current-change="handleCurrentChange"
        :current-page="currentPage"
        :page-sizes="[7, 14, 21, 28]"
        :page-size="pageSize"
        layout="total, sizes, prev, pager, next, jumper"
        :total="maxCount">
      </el-pagination>
    </div>
  </div>
    <div class="column one-third">
      <br/>
      <br/>
      <div style="width: 91%" @mouseleave="visible = false">
        <el-input
          placeholder="请输入内容"
          prefix-icon="el-icon-search"
          v-model="searchValue" @keyup.enter.native="search" >
        </el-input>
        <br/>
        <el-popover
          width="265"
          placement="bottom"
          trigger="manual"
          title="搜索结果"
          v-model="visible">
          <div v-for="item in serachResult" :key="item.index">
            <el-divider style="margin: 5px;padding: 0"/>
            <h4 @click="toDetail(item.id);visible=!visible">{{item.title}}</h4>
          </div>
        </el-popover>
      </div>
      <div>
      <h1 style="width: 91%">博客热门</h1>
      <div v-for="(item, index) in hotBlogs" :key="index" @click="toDetail(item.id)" class="hotBlogCard">
        <div style="width: 91%">
        <div class="card-body" :style="'background-image:'+img[index]"><h3>{{item.title}}</h3></div>
        <el-card @click="toDetail(blog.id)" shadow="hover">
          <h4><i class="el-icon-lollipop" /> <span>标题:</span>{{ item.title}}</h4>
          <a style="float: left">
          <i class="el-icon-date" />
            <span class="hotBlogDescription">发布时间:</span>
          <span class="hotBlogDescription">{{ item.publish_time }}</span>
          </a>
          <a style="float: right">
          <i class="el-icon-thumb"/>
            <span class="hotBlogDescription">点击量:</span>
          <span class="hotBlogDescription">{{ item.clicks }}</span>
          </a>
        </el-card>
        </div>
        <br />
    </div>
    </div>
    </div>
    </div>
    <el-backtop></el-backtop>
  </div>
</template>

<script>
export default {
  name: 'IndexContent',
  data () {
    return {
      visible: false,
      currentPage4: 4,
      indexblogs: [],
      maxCount: 1,
      currentPage: 1,
      pageSize: 7,
      hotBlogs: [],
      img: [
        'url("data:image/svg+xml;base64,")',
        'url("data:image/svg+xml;base64,")',
        'url("data:image/svg+xml;base64,")',
        'url("data:image/svg+xml;base64,")',
        'url("data:image/svg+xml;base64,")'

      ],
      searchValue: '',
      serachResult: []

    }
  },
  methods: {
    handleSizeChange (val) {
      this.pageSize = val
      this.getData()
    },
    handleCurrentChange (val) {
      this.currentPage = val
      this.getData()
    },
    getData () {
      this.axios.get('/api/listBlogs/?page=' + this.currentPage + '&page_size=' + this.pageSize)
        .then(response => {
          this.indexblogs = response.data.results
          this.maxCount = response.data.count
        })
    },
    getHotData () {
      this.axios.get('/api/hotBlogs/')
        .then(response => {
          this.hotBlogs = response.data.results
        })
    },
    toDetail (id) {
      const routeData = this.$router.resolve({
        name: 'detail',
        query: { id: id }
      })
      window.open(routeData.href, '_blank')
    },
    search () {
      if (this.searchValue === '') {
        this.$message.info('未输入搜索值')
      } else {
        this.visible = true
        this.axios.get('/api/searchBlogs/?param=' + this.searchValue)
          .then(response => {
            this.serachResult = response.data
          })
      }
    }
  },
  mounted () {
    this.getData()
    this.getHotData()
  }
}
</script>

